mapper.select:
SELECT * FROM ( select id,name, coalesce((select value from pb2_ext_ir_translation where name = 'res.fund,name' and type = 'model' and lang='th_TH' and res_id = id),name) as name_th from pb2_ext_res_fund order by id ) qry WHERE 1=1
concat(name, ' ', name_th) ILIKE '%'||#{item}||'%'
ORDER BY ${orderBy}

select f.id,f.name,coalesce(value,name) name_th
from pb2_ext_res_fund f inner join pb2_ext_res_fund_section_rel r on (f.id = r.fund_id and f.active = true)
	left join (
	select res_id,value
	from pb2_ext_ir_translation
	where name = 'res.fund,name'
	and type = 'model' and lang='th_TH'
	) ir on f.id = ir.res_id
where section_id = #{id}
ORDER BY ${orderBy}

select f.id,f.name,coalesce(value,name) name_th
from pb2_ext_res_fund f inner join pb2_ext_res_fund_project_rel r on (f.id = r.fund_id and f.active = true)
	left join (
	select res_id,value
	from pb2_ext_ir_translation
	where name = 'res.fund,name'
	and type = 'model' and lang='th_TH'
	) ir on f.id = ir.res_id
where project_id = #{id}
ORDER BY ${orderBy}

select f.id,f.name,coalesce(value,name) name_th
from pb2_ext_res_fund f inner join pb2_ext_res_fund_project_rel r on (f.id = r.fund_id and f.active = true)
	left join (
	select res_id,value
	from pb2_ext_ir_translation
	where name = 'res.fund,name'
	and type = 'model' and lang='th_TH'
	) ir on f.id = ir.res_id
where project_id = #{id}
ORDER BY ${orderBy}

select f.id,f.name,coalesce(value,name) name_th
from pb2_ext_res_fund f inner join pb2_ext_res_fund_project_rel r on (f.id = r.fund_id and f.active = true)
	left join (
	select res_id,value
	from pb2_ext_ir_translation
	where name = 'res.fund,name'
	and type = 'model' and lang='th_TH'
	) ir on f.id = ir.res_id
where project_id = #{id}
ORDER BY ${orderBy}
