mapper.select:
select e.employee_code, first_name,last_name ,po.name "position",s.name utype, e.position_id from pb2_ext_hr_employee e, pb2_ext_res_org o ,pb2_ext_res_section s,pb2_ext_hr_position po where e.org_id = o.id and e.section_id = s.id and e.position_id = po.id and e.section_id = #{code}
concat(e.employee_code, ' ', first_name, ' ', last_name, ' ', po.name, ' ', s.name, ' ', first_name_th, ' ', last_name_th) ILIKE '%'||#{item}||'%'
order by first_name,last_name

select e.employee_code, e.first_name, e.last_name, po.name "position" , concat('[',trim(p.code),'] ',p.name) utype, e.position_id from pb2_ext_res_project_member pm, pb2_ext_hr_employee e ,pb2_ext_hr_position po, pb2_ext_res_project p where e.id = pm.employee_id and e.position_id = po.id and pm.project_id = p.id and pm.project_id = #{code}
concat(e.employee_code, ' ', e.first_name, ' ', e.last_name, ' ', po.name, ' ', p.name, ' ',p.code) ILIKE '%'||#{item}||'%'
order by first_name,last_name
