mapper.select:
select sum(total) pre
from ( 
	select sum(total) as total
	from pb2_pcm_req
	where status in ('W1','W2')
	and  budget_cc = #{budgetCc}
	and  budget_cc_type= #{budgetCcType}   
	union
	select sum(total) as total
	from pb2_exp_use
	where status in ('W1','W2')
	and  budget_cc = #{budgetCc}
	and  budget_cc_type= #{budgetCcType}   
) qry
