mapper.select:
select * from ( select p.id,p.name, p.code, coalesce((select value from pb2_ext_ir_translation where name = 'res.project,name' and type = 'model' and lang='th_TH' and res_id = p.id), p.name) as name_th, o.name org_name, coalesce((select value from pb2_ext_ir_translation where name = 'res.org,name' and type = 'model' and lang='th_TH' and res_id = p.org_id), o.name) as org_name_th, lpad(e.employee_code, 6 ,'0') pm_code, e.first_name||' '||e.last_name pm_name , coalesce((select value from pb2_ext_ir_translation where name = 'hr.employee,first_name' and type = 'model' and lang='th_TH' and res_id = e.id), first_name) || ' ' || coalesce((select value from pb2_ext_ir_translation where name = 'hr.employee,last_name' and type = 'model' and lang='th_TH' and res_id = e.id), last_name) pm_name_th FROM pb2_ext_res_project p LEFT JOIN pb2_ext_res_org o on p.org_id = o.id LEFT JOIN pb2_ext_res_project_member m on m.project_id=p.id and m.project_position = 'manager' LEFT JOIN pb2_ext_hr_employee e on m.employee_id = e.id ) qry WHERE 1=1
concat(org_name, ' ', org_name_th, ' ', pm_code, ' ', pm_name, ' ' ,pm_name_th,' ', name, ' ', name_th, code) ILIKE '%'||#{item}||'%'
ORDER BY ${orderBy}
