mapper.select:
select * from ( select s.id, o.name ,o.name_th ,concat('[',trim(s.code),'] ',s.name) description ,concat('[',trim(s.code),'] ',s.name_th) description_th ,concat('[',trim(c.code),'] ',c.name) costcenter ,concat('[',trim(c.code),'] ',c.name_th) costcenter_th FROM (select org_id,costcenter_id,code,id,name, coalesce((select value from pb2_ext_ir_translation where name = 'res.section,name' and type = 'model' and lang='th_TH' and res_id = sec.id), name) as name_th from pb2_ext_res_section sec) s left join (select id, name, coalesce((select value from pb2_ext_ir_translation where name = 'res.org,name' and type = 'model' and lang='th_TH' and res_id = org.id), name) as name_th from pb2_ext_res_org org) o on s.org_id = o.id left join (select id,name,code, coalesce((select value from pb2_ext_ir_translation where name = 'res.costcenter,name' and type = 'model' and lang='th_TH' and res_id = cc.id), name) as name_th from pb2_ext_res_costcenter cc) c on s.costcenter_id = c.id ) qry WHERE 1=1
concat(name, ' ', description, ' ', costcenter, ' ', name_th, ' ', description_th, ' ' , costcenter_th) ILIKE '%'||#{item}||'%'
ORDER BY ${orderBy}
