mapper.select:
SELECT REQ.ID, total, objective_type, CASE WHEN #{lang}='' THEN mm.flag2 ELSE mm.name END objective_type_name, objective, budget_cc, budget_cc_type, CASE WHEN #{lang}='' THEN b.name ELSE b.name_th END budget_cc_name, WF_STATUS, STATUS, reason, cost_control_id, cost_control_type_id, WORKFLOW_INS_ID, TASK_ID, REQ.UPDATED_TIME, REQ.UPDATED_BY, REQ.CREATED_TIME, FOLDER_REF, DOC_REF, CASE WHEN STATUS='D' THEN '1' WHEN STATUS='W2' THEN '2' WHEN STATUS='W1' THEN '3' WHEN STATUS='S' THEN '4' WHEN STATUS='C1' THEN '5' WHEN STATUS='C2' THEN '6' WHEN STATUS='X1' THEN '7' WHEN STATUS='X2' THEN '8' ELSE STATUS END ORDER_FIELD, COALESCE(CBY ,REQ.CREATED_BY) CREATED_BY, COALESCE(RBY ,REQ_BY) REQ_BY, COUNT(*) OVER() totalRowCount FROM (SELECT ID, total, objective_type, objective, budget_cc, budget_cc_type, NULL WF_STATUS, STATUS, reason, cost_control_id, cost_control_type_id, WORKFLOW_INS_ID, NULL TASK_ID, UPDATED_TIME, UPDATED_BY, CREATED_TIME, CREATED_BY, (select CASE WHEN #{lang}='' THEN first_name ELSE coalesce((select value from pb2_ext_ir_translation where name = 'hr.employee,first_name' and type = 'model' and lang='th_TH' and res_id = e3.id), e3.first_name) END first_name from pb2_ext_hr_employee e3 where lpad(employee_code,6,'0') = pb2_exp_brw.created_by) CBY, FOLDER_REF, DOC_REF, REQ_BY, (select CASE WHEN #{lang}='' THEN first_name ELSE coalesce((select value from pb2_ext_ir_translation where name = 'hr.employee,first_name' and type = 'model' and lang='th_TH' and res_id = e4.id), e4.first_name) END first_name from pb2_ext_hr_employee e4 where lpad(employee_code,6,'0') = pb2_exp_brw.req_by) RBY FROM pb2_exp_brw WHERE STATUS='D' AND CREATED_BY=#{loginE} UNION SELECT ID, M.total, objective_type, objective, budget_cc, budget_cc_type, CASE WHEN (W.STATUS IS NULL) THEN '' ELSE CONCAT (W.BY ,'-', W.STATUS ,'-', to_char(W.BY_TIME, 'DD/MM/YYYY HH24:MI')) END WF_STATUS, M.STATUS, reason, cost_control_id, cost_control_type_id, M.WORKFLOW_INS_ID, TASK_ID, UPDATED_TIME, UPDATED_BY, CREATED_TIME, CREATED_BY, (select CASE WHEN #{lang}='' THEN first_name ELSE coalesce((select value from pb2_ext_ir_translation where name = 'hr.employee,first_name' and type = 'model' and lang='th_TH' and res_id = e1.id), e1.first_name) END first_name from pb2_ext_hr_employee e1 where lpad(employee_code,6,'0') = M.created_by) CBY, FOLDER_REF, DOC_REF, REQ_BY, (select CASE WHEN #{lang}='' THEN first_name ELSE coalesce((select value from pb2_ext_ir_translation where name = 'hr.employee,first_name' and type = 'model' and lang='th_TH' and res_id = e2.id), e2.first_name) END first_name from pb2_ext_hr_employee e2 where lpad(employee_code,6,'0') = M.req_by) RBY FROM pb2_exp_brw M LEFT JOIN ( SELECT MASTER_ID, WORKFLOW_INS_ID, STATUS, e.first_name as BY, BY_TIME, TASK_ID FROM pb2_main_workflow w left join pb2_ext_hr_employee e on w.by = lpad(e.employee_code,6,'0') WHERE TYPE='EXP_BRW' AND w.ID IN ( SELECT MAX(ID) FROM pb2_main_workflow GROUP BY MASTER_ID )) AS W ON M.ID = W.MASTER_ID WHERE M.STATUS != 'D' AND ( ID IN ( SELECT master_id FROM pb2_main_workflow_reviewer WHERE reviewer_user LIKE #{loginL}
reviewer_group LIKE #{item}
GROUP BY master_id ) OR id IN (SELECT master_id FROM pb2_main_workflow_next_actor WHERE actor_user LIKE #{loginE} GROUP BY master_id) OR CREATED_BY = #{loginE} OR REQ_BY = #{loginE} ) ) REQ LEFT JOIN ( select 'U' _type,id,concat('[',trim(code),'] ',name) "name", concat('[',trim(code),'] ',name_th) name_th from (select code,id,name, coalesce((select value from pb2_ext_ir_translation where name = 'res.section,name' and type = 'model' and lang='th_TH' and res_id = sec.id), name) as name_th from pb2_ext_res_section sec) s union select 'P' _type,id,concat('[',trim(code),'] ',name) "name", concat('[',trim(code),'] ',name_th) name_th from (select p.id,p.name, p.code, coalesce((select value from pb2_ext_ir_translation where name = 'res.project,name' and type = 'model' and lang='th_TH' and res_id = p.id), p.name) as name_th FROM pb2_ext_res_project p) pro ) b on (REQ.budget_cc_type = b._type and REQ.budget_cc = b.id) LEFT JOIN pb2_main_master mm on REQ.objective_type=mm.code and mm.type='BRW_TYPE' WHERE 1=1
concat(REQ.id, ' ', objective, ' ', reason) ILIKE '%'||#{item}||'%'
AND (REQ.id ilike #{searchTerm} OR objective ilike #{searchTerm} OR reason ilike #{searchTerm} )
AND status=#{status}
AND objective_type=#{objective_type}
ORDER BY ${orderBy} LIMIT #{limit} OFFSET #{start}

mapper.update:
     UPDATE pb2_exp_brw 
     SET 
     	workflow_ins_id=#{workflowInsId}

     		,status=#{status}

		,req_by =#{reqBy}
	    ,objective_type =#{objectiveType}
	    ,objective =#{objective}
	    ,reason =#{reason}
	    ,budget_cc =#{budgetCc}
	    ,budget_cc_type =#{budgetCcType}
	    ,fund_id =#{fundId}
	    ,cost_control_type_id =#{costControlTypeId}
	    ,cost_control_id =#{costControlId}
	    ,date_back =#{dateBack}
	    ,bank =#{bank}
	    ,bank_type =#{bankType}
	    ,total =#{total}
	    ,doc_ref =#{docRef}
	    ,folder_ref =#{folderRef}
	    ,waiting_level =#{waitingLevel}
        ,updated_by =#{updatedBy}
        ,updated_time =Now()
     WHERE id=#{id}

     UPDATE pb2_exp_brw 
     SET  

		WAITING_LEVEL=#{waitingLevel}


		, status=#{status}

     WHERE id=#{id}
