mapper.select:
SELECT REQ.ID, total, objective, budget_cc, budget_cc_type, b.name budget_cc_name, WF_STATUS, STATUS, cost_control_id, cost_control_type_id, cost_control, cost_control_from, cost_control_to, bank, bank_type, vat, vat_id, WORKFLOW_INS_ID, TASK_ID, REQ.UPDATED_TIME, REQ.UPDATED_BY, REQ.CREATED_TIME, FOLDER_REF, DOC_REF, CASE WHEN STATUS='D' THEN '1' WHEN STATUS='W2' THEN '2' WHEN STATUS='W1' THEN '3' WHEN STATUS='S' THEN '4' WHEN STATUS='C1' THEN '5' WHEN STATUS='C2' THEN '6' WHEN STATUS='X1' THEN '7' WHEN STATUS='X2' THEN '8' ELSE STATUS END ORDER_FIELD, COALESCE(CBY ,REQ.CREATED_BY) CREATED_BY, COALESCE(RBY ,REQ_BY) REQ_BY, mm.name pay_type_name, COUNT(*) OVER() totalRowCount FROM (SELECT ID, total, objective, budget_cc, budget_cc_type, NULL WF_STATUS, STATUS, cost_control_id, cost_control_type_id, cost_control, cost_control_from,cost_control_to, bank, bank_type, vat, vat_id, pay_type, WORKFLOW_INS_ID, NULL TASK_ID, UPDATED_TIME, UPDATED_BY, CREATED_TIME, CREATED_BY, (select first_name from pb2_ext_hr_employee where lpad(employee_code,6,'0') = pb2_exp_use.created_by) CBY, FOLDER_REF, DOC_REF, REQ_BY, (select first_name from pb2_ext_hr_employee where lpad(employee_code,6,'0') = pb2_exp_use.req_by) RBY FROM pb2_exp_use WHERE STATUS='D' AND CREATED_BY=#{loginE} UNION SELECT ID, M.total, objective, budget_cc, budget_cc_type, CASE WHEN (W.STATUS IS NULL) THEN '' ELSE CONCAT (W.BY ,'-', W.STATUS ,'-', to_char(W.BY_TIME, 'DD/MM/YYYY HH24:MI')) END WF_STATUS, M.STATUS, cost_control_id, cost_control_type_id, cost_control, cost_control_from,cost_control_to, bank, bank_type, vat, vat_id, pay_type, M.WORKFLOW_INS_ID, TASK_ID, UPDATED_TIME, UPDATED_BY, CREATED_TIME, CREATED_BY, (select first_name from pb2_ext_hr_employee where lpad(employee_code,6,'0') = M.created_by) CBY, FOLDER_REF, DOC_REF, REQ_BY, (select first_name from pb2_ext_hr_employee where lpad(employee_code,6,'0') = M.req_by) RBY FROM pb2_exp_use M LEFT JOIN ( SELECT MASTER_ID, WORKFLOW_INS_ID, STATUS, e.first_name as BY, BY_TIME, TASK_ID FROM pb2_main_workflow w left join pb2_ext_hr_employee e on w.by = lpad(e.employee_code,6,'0') WHERE TYPE='EXP_USE' AND w.ID IN ( SELECT MAX(ID) FROM pb2_main_workflow GROUP BY MASTER_ID )) AS W ON M.ID = W.MASTER_ID WHERE M.STATUS != 'D' AND ( ID IN ( SELECT master_id FROM pb2_main_workflow_reviewer WHERE reviewer_user LIKE #{loginL}
reviewer_group LIKE #{item}
GROUP BY master_id ) OR id IN (SELECT master_id FROM pb2_main_workflow_next_actor WHERE actor_user LIKE #{loginE} GROUP BY master_id) OR CREATED_BY = #{loginE} OR REQ_BY = #{loginE} ) ) REQ LEFT JOIN ( select 'U' _type,id,name from pb2_ext_res_section union select 'P' _type,id,name from pb2_ext_res_project ) b on (REQ.budget_cc_type = b._type and REQ.budget_cc = b.id) LEFT JOIN pb2_main_master mm on REQ.pay_type=mm.code and mm.type='EXP_TYPE' WHERE 1=1
AND (REQ.id ilike #{searchTerm} OR objective ilike #{searchTerm} )
AND status=#{status}
ORDER BY ${orderBy} LIMIT #{limit} OFFSET #{start}

mapper.update:
     UPDATE pb2_exp_use 
     SET 
     	workflow_ins_id=#{workflowInsId}

     		,status=#{status}

		,req_by =#{reqBy}
		,req_section_id =#{reqSectionId}
	    ,objective =#{objective}
	    ,budget_cc =#{budgetCc}
	    ,budget_cc_type =#{budgetCcType}
	    ,cost_control_type_id =#{costControlTypeId}
	    ,cost_control_id =#{costControlId}
	    ,cost_control =#{costControl}
	    ,cost_control_from =#{costControlFrom}
	    ,cost_control_to =#{costControlTo}
	    ,bank_type =#{bankType}
	    ,bank =#{bank}
	    ,vat =#{vat}
	    ,vat_id =#{vatId}
	    ,pay_type =#{payType}
	    ,pay_dtl1 =#{payDtl1}
	    ,pay_dtl2 =#{payDtl2}
	    ,pay_dtl3 =#{payDtl3}
	    ,total =#{total}
	    ,doc_ref =#{docRef}
	    ,folder_ref =#{folderRef}
	    ,waiting_level =#{waitingLevel}
        ,updated_by =#{updatedBy}
        ,updated_time =Now()
     WHERE id=#{id}

     UPDATE pb2_exp_use 
     SET  

		WAITING_LEVEL=#{waitingLevel}


		, status=#{status}

     WHERE id=#{id}
