mapper.select:
SELECT REQ.ID, total, objective_type, objective, budget_cc, budget_cc_type, b.name budget_cc_name, WF_STATUS, STATUS, reason, cost_control_id, cost_control_type_id, location, prweb_method_id, method_cond2, method_cond2_rule, method_cond2_dtl, WORKFLOW_INS_ID, TASK_ID, UPDATED_TIME, UPDATED_BY, CREATED_TIME, FOLDER_REF, DOC_REF, CASE WHEN STATUS='D' THEN '1' WHEN STATUS='W2' THEN '2' WHEN STATUS='W1' THEN '3' WHEN STATUS='S' THEN '4' WHEN STATUS='C1' THEN '5' WHEN STATUS='C2' THEN '6' WHEN STATUS='X1' THEN '7' WHEN STATUS='X2' THEN '8' ELSE STATUS END ORDER_FIELD, COALESCE(CBY ,CREATED_BY) CREATED_BY, COALESCE(RBY ,REQ_BY) REQ_BY, COUNT(*) OVER() totalRowCount FROM (SELECT ID, total, objective_type, objective, budget_cc, budget_cc_type, NULL WF_STATUS, STATUS, reason, cost_control_id, cost_control_type_id, location, prweb_method_id, method_cond2, method_cond2_rule, method_cond2_dtl, WORKFLOW_INS_ID, NULL TASK_ID, UPDATED_TIME, UPDATED_BY, CREATED_TIME, CREATED_BY, (select first_name from pb2_ext_hr_employee where lpad(employee_code,6,'0') = pb2_pcm_req.created_by) CBY, FOLDER_REF, DOC_REF, REQ_BY, (select first_name from pb2_ext_hr_employee where lpad(employee_code,6,'0') = pb2_pcm_req.req_by) RBY FROM pb2_pcm_req WHERE STATUS='D' AND CREATED_BY=#{loginE} UNION SELECT ID, M.total, objective_type, objective, budget_cc, budget_cc_type, CASE WHEN (W.STATUS IS NULL) THEN '' ELSE CONCAT (W.BY ,'-', W.STATUS ,'-', to_char(W.BY_TIME, 'DD/MM/YYYY HH24:MI')) END WF_STATUS, M.STATUS, reason, cost_control_id, cost_control_type_id, location, prweb_method_id, method_cond2, method_cond2_rule, method_cond2_dtl, M.WORKFLOW_INS_ID, TASK_ID, UPDATED_TIME, UPDATED_BY, CREATED_TIME, CREATED_BY, (select first_name from pb2_ext_hr_employee where lpad(employee_code,6,'0') = M.created_by) CBY, FOLDER_REF, DOC_REF, REQ_BY, (select first_name from pb2_ext_hr_employee where lpad(employee_code,6,'0') = M.req_by) RBY FROM pb2_pcm_req M LEFT JOIN ( SELECT MASTER_ID, WORKFLOW_INS_ID, STATUS, e.first_name as BY, BY_TIME, TASK_ID FROM pb2_main_workflow w left join pb2_ext_hr_employee e on w.by = lpad(e.employee_code,6,'0') WHERE TYPE='PCM_REQ' AND w.ID IN ( SELECT MAX(ID) FROM pb2_main_workflow GROUP BY MASTER_ID )) AS W ON M.ID = W.MASTER_ID WHERE M.STATUS != 'D' AND ( ID IN ( SELECT master_id FROM pb2_main_workflow_reviewer WHERE reviewer_user LIKE #{loginL}
reviewer_group LIKE #{item}
GROUP BY master_id ) OR id IN (SELECT master_id FROM pb2_main_workflow_next_actor WHERE actor_user LIKE #{loginE} GROUP BY master_id) OR CREATED_BY = #{loginE} OR REQ_BY = #{loginE} ) ) REQ LEFT JOIN ( select 'U' _type,id,name from pb2_ext_res_section union select 'P' _type,id,p.name||' - '||p.description from pb2_ext_res_project p ) b on (REQ.budget_cc_type = b._type and REQ.budget_cc = b.id)
WHERE 1=1
AND (REQ.id ilike #{searchTerm} OR objective ilike #{searchTerm} OR reason ilike #{searchTerm} OR location ilike #{searchTerm} OR method_cond2_rule ilike #{searchTerm} OR method_cond2 ilike #{searchTerm} OR method_cond2_dtl ilike #{searchTerm} )
AND status=#{status}
AND objective_type=#{objective_type}
ORDER BY ${orderBy} LIMIT #{limit} OFFSET #{start}

mapper.update:
     UPDATE pb2_pcm_req 
     SET 
     	workflow_ins_id=#{workflowInsId}

     		,status=#{status}

		,req_by =#{reqBy}
		,req_section_id =#{reqSectionId}
	    ,objective_type =#{objectiveType}
	    ,objective =#{objective}
	    ,reason =#{reason}
	    ,currency =#{currency}
	    ,currency_rate =#{currencyRate}
	    ,budget_cc =#{budgetCc}
	    ,budget_cc_type =#{budgetCcType}
	    ,is_stock =#{isStock}
	    ,stock_section_id =#{stockSectionId}
	    ,is_prototype =#{isPrototype}
	    ,prototype =#{prototype}
	    ,prototype_contract_no =#{prototypeContractNo}
	    ,cost_control_type_id =#{costControlTypeId}
	    ,cost_control_id =#{costControlId}
	    ,pcm_section_id =#{pcmSectionId}
	    ,location =#{location}
	    ,is_across_budget =#{isAcrossBudget}
	    ,across_budget =#{acrossBudget}
	    ,is_ref_id =#{isRefId}
	    ,ref_id =#{refId}
	    ,prweb_method_id =#{prWebMethodId}
	    ,method_cond2_rule =#{methodCond2Rule}
	    ,method_cond2 =#{methodCond2}
	    ,method_cond2_dtl =#{methodCond2Dtl}
	    ,vat =#{vat}
	    ,vat_id =#{vatId}
	    ,total =#{total}
	    ,doc_ref =#{docRef}
	    ,folder_ref =#{folderRef}
	    ,waiting_level =#{waitingLevel}
        ,updated_by =#{updatedBy}
        ,updated_time =Now()
     WHERE id=#{id}

mapper.select:
SELECT *, b.name budget_cc_name, c.name cost_control_name, ct.name cost_control_type_name, CASE WHEN budget_cc_type='U' THEN 'หน่วยงาน' WHEN budget_cc_type='P' THEN 'โครงการ' ELSE budget_cc_type END budget_cc_type_name FROM pb2_pcm_req r LEFT JOIN ( select 'U' _type,id,name from pb2_ext_res_section union select 'P' _type,id,p.name||' - '||p.description as name from pb2_ext_res_project p ) b on (r.budget_cc_type = b._type and r.budget_cc = b.id)
LEFT JOIN pb2_ext_cost_control c on (r.cost_control_id = c.id) LEFT JOIN pb2_ext_cost_control_type ct on (r.cost_control_type_id = ct.id)
WHERE r.id=#{id}

mapper.update:
     UPDATE pb2_pcm_req 
     SET  

		WAITING_LEVEL=#{waitingLevel}


		, status=#{status}

     WHERE id=#{id}
